1. 实现一个Promise.all。(中级)
function PromiseAll(promises) {
  return new Promise(function (resolve, reject) {
    const len = promises.length;
    const result = new Array(len);
    let successLength = 0;
    promises.forEach((p, i) => {
      p.then((data) => {
        result[i] = data;
        successLength++;
        if (len === successLength) {
          resolve(result);
        }
      }).catch(reject);
    });
  });
}

